    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
        
        String val1=jTextField1.getText().toString();
        String val2=jTextField2.getText().toString();
        String val3=jTextField3.getText().toString();
        String val4=jTextField4.getText().toString();
        
        
   
        if(val1.isEmpty() || val2.isEmpty() || val3.isEmpty() || val4.isEmpty()){
            error.setText("All Fields Must Have Some Value");
            
        }
        else{
           resultLabel.setText(Rs.reservation(val1, val2, val3, Integer.parseInt(val4)));
        jFrame3.setVisible(true);
        jFrame4.setVisible(false);
        }
    }//GEN-LAST:event_jButton7ActionPerformed

