    private void resListActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resListActionPerformed
        // TODO add your handling code here:
        ReservationContact Rc;
        jTextField5.setText("");
        for(int i=0;i<Rs.getReservationContact().size();i++){
            Rc=Rs.getReservationContact().get(i);
            System.out.println(Rc.getName());
            jTextField5.setText(jTextField5.getText().toString()+"\r\nName:"+ Rc.getName() + "; Time:" + Rc.getTime() + "; People:"+Rc.getPersonReserved());
           
        }
        jFrame5.setVisible(true);
        jFrame1.setVisible(false);
    }//GEN-LAST:event_resListActionPerformed

   
    private void populateList(){
        Vector<String> ar=Rs.getMyRes().getAppetizers();
        Vector<String> sou=Rs.getMyRes().getSoups();
        
        Vector<String> SD=Rs.getMyRes().getSideDishes();
        Vector<String> des=Rs.getMyRes().getDesserts();
        Vector<String> mai=Rs.getMyRes().getMainCourse();
        
       jLabel3.setText(ar.get(0));
       jLabel4.setText(ar.get(1));
       jLabel5.setText(ar.get(2));
       jLabel6.setText(ar.get(3));
            
       jList1.setListData(sou);
       jList2.setListData(SD);
       jList3.setListData(des);
       jList4.setListData(mai);
    
    }
    
